import React, { useEffect, useState } from 'react'
import "../Featured/Featured.scss"
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import InfoOutlinedIcon from '@mui/icons-material/InfoOutlined';
import axios from "axios"

export default function Featured({type}) {
    const [content, setContent] = useState({});

    useEffect(()=>{
        const getRandomContent = async ()=>{
            try {
                const res = await axios.get(`/movies/random?type=${type}`, { headers:  {
                    token: "bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzOTc0YzkxMWE0NGU5MTYzZjE1MzE0OSIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY3MDg2MDY0MSwiZXhwIjoxNjcxMjkyNjQxfQ.qBYLfeqGmwrCnTr2Mu7ij2OJNhGgsnOM0XUcJbN-S5w"
                }})
                setContent(res.data[0])
            } catch (error) {
                console.log(error);
            }
        }
        getRandomContent()

    },[type])

  return (
    <div className='featured'>
        {type && (
            <div className="category">
                <span>{type === "movies" ? "Movies" : "Series"}</span>
                <select name="genre" id="genre">
                    <option value="comedy">comedy</option>
                    <option value="crime">crime</option>
                    <option value="fantasy">fantasy</option>
                    <option value="historical">Historical</option>
                    <option value="horror">Horror</option>
                    <option value="romance">Romance</option>
                    <option value="sci-fi">Sci-fi</option>
                    <option value="thriller">Thriller</option>
                    <option value="animation">Animation</option>
                    <option value="drama">Drama</option>
                    <option value="documentry">Documentry</option>
                </select>
            </div>
        )}
        <img width="100%" src={content.img} alt="" />
        <div className="info">
            <img src={content.imgTitle} alt="" />
            <span className='desc'>
            {content.desc}
            </span>
            <div className="buttons">
                <button className="play">
                    <PlayArrowIcon/>
                    <span>Play</span>
                </button>
                <button className="more">
                    <InfoOutlinedIcon/>
                    <span>Info</span>
                </button>
            </div>
        </div>
    </div>
  )
}
