import "./app.scss"
import Home from "./pages/Home/Home";
import Login from "./pages/login/Login";
import Register from "./pages/register/Register";
import Watch from "./pages/Watch/Watch";

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Navigate
} from "react-router-dom";
import {Routes} from 'react-router-dom';

const App =()=> {
  const user = true;
  return (

    <Router>
        <Routes>
          <Route exact path="/" element={user ? <Home/> : <Navigate to="register"/>} />
          <Route exact path="/login" element={!user ? <Login/> : <Navigate to="register"/>} />
          <Route exact path="/register" element={!user ? <Register/> : <Navigate to="/"/>} />
          {user&&(
            <>
              <Route exact path="/movies" element={<Home type="movies"/>} />
              <Route exact path="/series" element={<Home type="series"/>} />
              <Route exact path="/watch" element={<Watch/>} />
            </>
          )
          }

         </Routes>
   </Router>
   )
}

export default App;
