import React, { useEffect, useState } from 'react'
import Featured from '../../components/Featured/Featured.jsx'
import List from '../../components/List/List.jsx'
import Navbar from '../../components/Navbar/Navbar'
import "./home.scss"
import axios from "axios"


const Home = ({type}) => {
  const [lists, setLists] = useState([])
  const [genre, setGenre] = useState(null)

  useEffect(()=>{
    const getRandomLists = async ()=>{
      try {
        const res =  await axios.get(`lists${type ? "&type=" + type : ""}${genre ? "&genre=" + genre : ""}`,{
          headers:  {
            token: "bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzOTc0YzkxMWE0NGU5MTYzZjE1MzE0OSIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY3MDkyMTI3NiwiZXhwIjoxNjcxMzUzMjc2fQ.pIn7jkH5cPpLrtqiy8Lw97BsKTgM_RBWAVia4n-rLpo"
          },
        });
        // console.log(res.data);
        setLists(res.data)
      } catch (error) {
        console.log(error);
      }
    }
    getRandomLists();
  },[type, genre])
  return (
    <div className='home'>
      <Navbar/>      
      <Featured type={type}/>
      {lists.map((list)=>{
        <List list={list}/>

      })}
    </div>
  )
}

export default Home
