import './ListItem.scss'
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import AddIcon from '@mui/icons-material/Add';
import ThumbUpOutlinedIcon from '@mui/icons-material/ThumbUpOutlined';
import ThumbDownOutlinedIcon from '@mui/icons-material/ThumbDownOutlined';
import { useEffect, useState } from 'react';
import axios from "axios"
export default function ListItem({index, item}) {
  const [isHovered, setIsHovered] = useState(false)

  useEffect(()=>{
    const getMovie = async ()=>{
      try {
        const res = await axios.get("/movies/find/"+ item,  {headers:  {
          token: "bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzOTc0YzkxMWE0NGU5MTYzZjE1MzE0OSIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY3MDkyMTI3NiwiZXhwIjoxNjcxMzUzMjc2fQ.pIn7jkH5cPpLrtqiy8Lw97BsKTgM_RBWAVia4n-rLpo"
        }, }
        )
      } catch (error) {
        console.log(error);
      }
    }
  },[])

  return (
    <div className='ListItem' style={{left: isHovered && index * 225 - 50 + index * 2.5}} onMouseEnter={()=>setIsHovered(true)} onMouseLeave={()=>setIsHovered(false)}>
      <img src={item.img} alt="" />
      {isHovered && (
        <>
         <video src={item.trailer} autoPlay={true} loop />
         <div className="itemInfo">
           <div className="icons">
             <PlayArrowIcon className='icon'/>
             <AddIcon className='icon'/>
             <ThumbUpOutlinedIcon className='icon'/>
             <ThumbDownOutlinedIcon className='icon'/>
           </div>
           <div className="itemInfoTop">
             <span>{item.duration}</span>
             <span className='limit'>+{item.limit}</span>
             <span>{item.year}</span>
           </div>
           <div className="desc">
             {item.desc}
           </div>
           <div className="genre">{item.genre}</div>
         </div>
        </>
      )}
    </div>
  )
}
